import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import CanvasDraw from 'react-canvas-draw';
import axios from 'axios';

function App() {
  const [imageSrc, setImageSrc] = useState(null);
  const [imageWidth, setImageWidth] = useState(0);
  const [imageHeight, setImageHeight] = useState(0);
  const [brushSize, setBrushSize] = useState(5);
  const canvasRef = useRef(null);
  const [imageFile, setImageFile] = useState(null);

  useEffect(() => {
    if (canvasRef.current) {
      canvasRef.current.clear();
    }
  }, [imageSrc]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
    const reader = new FileReader();
    reader.onloadend = () => {
      setImageSrc(reader.result);
      const img = new Image();
      img.onload = () => {
        setImageWidth(img.width);
        setImageHeight(img.height);
      };
      img.src = reader.result;
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleExport = () => {
    if (canvasRef.current) {
      const maskDataUrl = canvasRef.current.getDataURL('image/png');
      const link = document.createElement('a');
      link.href = maskDataUrl;
      link.download = 'mask.png';
      link.click();
    }
  };

  const handleUpload = async () => {
    if (!imageFile || !canvasRef.current) return;
    const maskDataUrl = canvasRef.current.getDataURL('image/png');
    const maskBlob = await fetch(maskDataUrl).then(res => res.blob());

    const formData = new FormData();
    formData.append('file', imageFile);
    formData.append('mask', maskBlob, 'mask.png');

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Image and Mask uploaded:', response.data);
    } catch (error) {
      console.error('Error uploading image and mask:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Image Inpainting Widget</h1>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {imageSrc && (
          <div>
            {imageSrc && (
              <CanvasDraw
                ref={canvasRef}
                imgSrc={imageSrc}
                brushColor="white"
                brushRadius={brushSize}
                canvasWidth={imageWidth}
                canvasHeight={imageHeight}
              />
            )}
            <div>
              <button onClick={() => setBrushSize(Math.max(1, brushSize + 1))}>Increase Brush Size</button>
              <button onClick={() => setBrushSize(Math.max(1, brushSize - 1))}>Decrease Brush Size</button>
            </div>
            <button onClick={handleExport}>Save Mask</button>
            <div>
              <h3>Original Image</h3>
              <img src={imageSrc} alt="Original" width="300" />
            </div>
            <button onClick={handleUpload}>Upload Image and Mask</button>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
