import React, { useRef, useState } from 'react';
import CanvasDraw from 'react-canvas-draw';

const MaskDrawing = ({ imageSrc }) => {
  const [brushSize, setBrushSize] = useState(5);
  const canvasRef = useRef(null);

  return (
    <div>
      <CanvasDraw
        ref={canvasRef}
        brushColor="white"
        brushRadius={brushSize}
        imgSrc={imageSrc}
      />
      <button onClick={() => setBrushSize(brushSize + 1)}>Increase Brush</button>
      <button onClick={() => setBrushSize(brushSize - 1)}>Decrease Brush</button>
    </div>
  );
};
